def removeDuplicates(arr):
    # 建立一個空列表
    res = []

    # 遍歷原始陣列
    for i in arr:
        # 如果元素還沒有在結果中，則新增它
        if i not in res:
            res.append(i)

    # 返回結果
    return res

def calculateFactorial(n: int) -> int: 
    '''返回n的階乘（n!）

    引數：
        n（int）：要計算階乘的數字。

    返回：
        int：n的階乘（n!）
    '''
    # 基本情況：0！= 1
    if n == 0: 
        return 1
    # 遞迴情況：n！= n *（n-1）！
    else: 
        return n * calculateFactorial(n-1)
